mapper.insert:
insert into w_user(utype, username, upassword, uemail, createTime, forbidden)
        VALUES (#{type, jdbcType=VARCHAR}, #{username, jdbcType=VARCHAR},
                #{password, jdbcType=VARCHAR}, #{email, jdbcType=VARCHAR},
                #{createTime, jdbcType=VARCHAR}, #{forbidden, jdbcType=VARCHAR}
        )

mapper.update:
UPDATE w_user SET username = #{username}, upassword = #{password}
, uemail = #{email}
WHERE uid = #{uid}

mapper.select:
SELECT * FROM w_user
ORDER BY uid DESC
LIMIT #{offset}, #{limit}
