mapper.insert:
insert into w_bottle(aid, btime, btype, blookTime, comment_sum, title, content, private)
        VALUES (#{aid, jdbcType=INTEGER}, #{time, jdbcType=INTEGER}, #{type, jdbcType=VARCHAR}
        , #{lookTimes, jdbcType=INTEGER}, #{comment_sum, jdbcType=INTEGER}, #{title, jdbcType=VARCHAR}
        , #{content, jdbcType=VARCHAR}, #{isPrivate, jdbcType=VARCHAR})

mapper.update:
UPDATE w_bottle
SET blookTime = blookTime + 1
WHERE bid = #{bid}

UPDATE w_bottle
SET comment_sum = comment_sum + 1
WHERE bid = #{bid}

UPDATE w_bottle
SET comment_sum = comment_sum - 1
WHERE bid = #{bid}

mapper.select:
SELECT *
FROM w_bottle
WHERE aid = #{aid}
ORDER BY blookTime DESC
LIMIT #{offset}, #{limit}
