mapper.insert:
insert into w_user(uid, utype, uname, upassword, uemail)
        VALUES (#{uid, jdbcType=INTEGER}, #{type, jdbcType=VARCHAR}, #{username, jdbcType=VARCHAR},
                #{password, jdbcType=VARCHAR}, #{email, jdbcType=VARCHAR}
        )

